// Update Cart Count
function updateTotalCartCount(cnt) {
	var pattern = /^[0-9]+$/;
	if(!pattern.test(cnt)){	cnt = 0; }
	if(cnt > 99){ cnt = 99; }
	$('.gnb-cart-count').html('<span class="hidden">' + $("#productCountText").val() + ' : </span>' + cnt);
	if(cnt > 0){
		$('.gnb-cart-count').show();
		ss.Auth.setCartEl(true);
	} else {
		$('.gnb-cart-count').hide();
		ss.Auth.setCartEl(false);
	}
}

